import React, { useState, useEffect } from "react";
import "./App.css";

const GRID_ROWS = 15;
const GRID_COLS = 20;

const App = () => {
  const [waveIndex, setWaveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setWaveIndex((prev) => (prev + 1) % GRID_COLS);
    }, 200); // Change the wave every 200ms
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="grid-container">
      {Array.from({ length: GRID_ROWS }).map((_, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {Array.from({ length: GRID_COLS }).map((_, colIndex) => (
            <div
              key={colIndex}
              className={`grid-cell ${
                colIndex === waveIndex ? "active" : ""
              }`}
              style={{
                backgroundColor:
                  colIndex === waveIndex
                    ? `rgb(${100 + rowIndex * 10}, 0, ${255 - rowIndex * 10})`
                    : "black",
              }}
            ></div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default App;
