import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const rows = 15;
  const cols = 20;

  // State to track the wave position
  const [waveIndex, setWaveIndex] = useState(0);

  // Create the grid
  const grid = [];
  for (let row = 0; row < rows; row++) {
    const rowCells = [];
    for (let col = 0; col < cols; col++) {
      rowCells.push({ row, col });
    }
    grid.push(rowCells);
  }

  // Update wave position periodically
  useEffect(() => {
    const interval = setInterval(() => {
      setWaveIndex((prev) => (prev + 1) % cols);
    }, 200);
    return () => clearInterval(interval);
  }, [cols]);

  return (
    <div className="app">
      <h1>Dynamic Wave Animation</h1>
      <div className="grid-container">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell) => {
              const isWave = Math.abs(cell.col - waveIndex) === 0;
              return (
                <div
                  key={`${cell.row}-${cell.col}`}
                  className={`cell ${isWave ? "wave" : ""}`}
                ></div>
              );
            })}
          </div>
        ))}
      </div>
      <div className="game-footer">
        Enjoy the game-like wave animation effect! ðŸš€
      </div>
    </div>
  );
}

export default App;
