import React, { useState, useEffect } from "react";
import "./Grid.css";

const Grid = ({ rows, columns }) => {
  const [grid, setGrid] = useState([]);

  useEffect(() => {
    // Initialize grid with default colors
    const createGrid = () => {
      const newGrid = Array.from({ length: rows }, () =>
        Array.from({ length: columns }, () => "black")
      );
      setGrid(newGrid);
    };

    createGrid();
  }, [rows, columns]);

  useEffect(() => {
    const animateGrid = () => {
      setGrid((prevGrid) =>
        prevGrid.map((row, rowIndex) =>
          row.map((cell, colIndex) => {
            // Calculate wave pattern
            const wave =
              Math.sin((colIndex + Date.now() / 1000) * 0.5) *
              Math.cos((rowIndex + Date.now() / 1000) * 0.5);

            const intensity = Math.abs(wave);
            return `rgb(${intensity * 255}, 0, ${255 - intensity * 255})`; // Purple to black transition
          })
        )
      );
    };

    // Add a timeout instead of a fast interval
    const timeout = setTimeout(animateGrid, 300); // Color change every 300ms
    return () => clearTimeout(timeout); // Cleanup on unmount
  }, [grid]);

  return (
    <div
      className="grid"
      style={{
        gridTemplateColumns: `repeat(${columns}, 1fr)`,
      }}
    >
      {grid.map((row, rowIndex) =>
        row.map((color, colIndex) => (
          <div
            key={`${rowIndex}-${colIndex}`}
            className="cell"
            style={{ backgroundColor: color }}
          ></div>
        ))
      )}
    </div>
  );
};

export default Grid;
