import React, { useState } from 'react';
import GridComponent from './GridComponent';

const UIComponent = () => {
  const [gridSize, setGridSize] = useState(15);
  const [waveSpeed, setWaveSpeed] = useState(1);
  const [colorChanges, setColorChanges] = useState(10);

  return (
    <div className="ui-container">
      <h1>Bouncing Wave Grid</h1>
      <div className="controls">
        <label>Grid Size:</label>
        <input
          type="number"
          value={gridSize}
          onChange={(e) => setGridSize(parseInt(e.target.value))}
        />
        <label>Wave Speed:</label>
        <input
          type="number"
          value={waveSpeed}
          onChange={(e) => setWaveSpeed(parseInt(e.target.value))}
        />
        <label>Color Changes per Second:</label>
        <input
          type="number"
          value={colorChanges}
          onChange={(e) => setColorChanges(parseInt(e.target.value))}
        />
      </div>
      <GridComponent
        gridSize={gridSize}
        waveSpeed={waveSpeed}
        colorChanges={colorChanges}
      />
    </div>
  );
};

export default UIComponent;
