import React, { useState } from 'react';
import Grid from './Grid';
import './App.css';

const App = () => {
  const [running, setRunning] = useState(true);

  const toggleWave = () => setRunning(!running);

  return (
    <div className="app">
      <h1>Wave Pattern Game</h1>
      <Grid rows={15} columns={20} running={running} />
      <button onClick={toggleWave}>{running ? 'Stop' : 'Start'} Wave</button>
    </div>
  );
};

export default App;
