import React, { useState, useEffect } from 'react';
import './Grid.css';

const Grid = ({ rows, columns }) => {
  const [grid, setGrid] = useState([]);

  useEffect(() => {
    const createGrid = () => {
      const newGrid = Array.from({ length: rows }, (_, rowIndex) =>
        Array.from({ length: columns }, (_, colIndex) => ({
          row: rowIndex,
          col: colIndex,
          color: 'gray',
        }))
      );
      setGrid(newGrid);
    };
    createGrid();
  }, [rows, columns]);

  useEffect(() => {
    const interval = setInterval(() => {
      setGrid((prevGrid) =>
        prevGrid.map((row, rowIndex) =>
          row.map((cell, colIndex) => {
            const waveEffect = Math.sin((rowIndex + colIndex + Date.now() / 200) * 0.2);
            const intensity = Math.abs(waveEffect);
            const color = `rgb(${Math.floor(intensity * 255)}, ${Math.floor(
              (1 - intensity) * 255
            )}, 150)`;
            return { ...cell, color };
          })
        )
      );
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="grid"
      style={{
        display: 'grid',
        gridTemplateColumns: `repeat(${columns}, 30px)`,
        gap: '2px',
      }}
    >
      {grid.map((row) =>
        row.map((cell) => (
          <div
            key={`${cell.row}-${cell.col}`}
            style={{
              width: '30px',
              height: '30px',
              backgroundColor: cell.color,
            }}
          ></div>
        ))
      )}
    </div>
  );
};

export default Grid;
